psql game_scout_db -c "\copy (select * from developers inner join developer_snapshots on developer_snapshots.developer_id = developers.id left join games on developers.username = ANY(games.developers) where not developers.disabled) to '$(pwd)/$(date +%F)_sample.csv' (FORMAT CSV, HEADER TRUE, DELIMITER ',', ENCODING 'UTF8');"
psql game_scout_db -c "\copy (select * from steam_apps where not bare and weighted_score is not null) to '$(pwd)/$(date +%F)_steamapps.csv' (FORMAT CSV, HEADER TRUE, DELIMITER ',', ENCODING 'UTF8');"
